INSERT INTO `wp_posts` VALUES("8", "1", "2019-02-08 20:01:35", "2019-02-08 20:01:35", "<div class=\"banner\">\n<p><img class=\"alignnone wp-image-468 size-full\" src=\"http://localhost:8888/Online-Portfolio-2019/wp-content/uploads/2019/04/Banner-Image-11a.jpg\" alt=\"\" width=\"1357\" height=\"819\" /></p>\n<div class=\"bannerContent\">\n<h1>Crafted Digital</h1>\n<p>Beautifully crafted online experiences. I work with clients all over the world. Lets create something amazing together.</p>\n<p><a href=\"http://localhost:8888/Online-Portfolio-2019/contact/\" data-wplink-url-error=\"true\"><button class=\"contact-btn\">Start your project</button></a></p>\n<div class=\"scrollDown\">See my work</div>\n</div>\n</div>\n\n<!-- wp:html -->\n<div class=\"introWidth\"><h2 class=\"home-intro\"><span class=\"colorText\">Hello.</span> My name is Niall. I am a London based designer with 10+ years\' experience in web and graphic design</h2></div>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<div class=\"introWidth\"><p class=\"home-intro\">I help businesses make an impact online through user-friendly, engaging, and responsive websites. I work closely with you to create an engaging online experience thats conveys your goals and vision</p></div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<div class=\"workSection\">\n<h2>Featured work</h2>\n<div class=\"workContainer\">[insert page=\'project-01\' display=\'page-work-snippets.php\'][insert page=\'project-02\' display=\'page-work-snippets.php\'][insert page=\'project-03\' display=\'page-work-snippets.php\'][insert page=\'project-04\' display=\'page-work-snippets.php\'][insert page=\'project-05\' display=\'page-work-snippets.php\'][insert page=\'project-06\' display=\'page-work-snippets.php\']</div>\n<p></p>\n</div>\n\n<!-- wp:html -->\n<div class=\"infoSection\">\n<div class=\"aboutMe\">\n<h2>About me</h2>\n<p>I design and build functional and beautiful websites that further your companys goals and vision. I believe the experience of creating a new website should be an enjoyable and inspiring one for you. If you have a project in mind that youd like to discuss or if you are a design agency looking to fill a freelance position please dont hesitate to get in touch.</p>\n</div>\n<div class=\"testimonials\">\n<h2 class=\"testimonialHeader\">What my clients say</h2>\n[testimonial_view id=\"1\"]\n</div>\n</div>\n<!-- /wp:html -->\n\n<div class=\"displayImages\">\n<div class=\"displayImage\"><img class=\"alignnone wp-image-470 size-full\" src=\"http://localhost:8888/Online-Portfolio-2019/wp-content/uploads/2019/04/Display-Image-01.png\" alt=\"\" width=\"1294\" height=\"774\"></div>\n<div class=\"displayImage\"><img class=\"alignnone wp-image-473 size-full\" src=\"http://localhost:8888/Online-Portfolio-2019/wp-content/uploads/2019/04/Display-Image-02a.jpg\" alt=\"\" width=\"610\" height=\"774\"></div>\n<div class=\"displayImage\"><img class=\"alignnone wp-image-535 size-full\" src=\"http://localhost:8888/Online-Portfolio-2019/wp-content/uploads/2019/04/Display-Image-03-2.png\" alt=\"\" width=\"610\" height=\"774\"></div>\n</div>", "home", "", "publish", "closed", "closed", "", "home", "", "", "2019-04-20 16:25:00", "2019-04-20 16:25:00", "", "0", "http://localhost:8888/Online-Portfolio-2019/?page_id=8", "0", "page", "", "0");
